function showGraph(stadium, seat) {
  const data = stadiums[stadium];
  const categoryData = category[stadium];
  const info = categoryData.find(p => p.카테고리 === seat.구역);

  // 🔹 Use pre-binned histogram data
  const bins = ticketData[stadium][seat.구역];

  const originalPrice = info['평균원가'];
  // Median is tricky now — either compute it in Python & store, 
  // or approximate from bins. For now: use 중앙가격 if you saved it.
  const medianPrice = info['중앙가격'];

  d3.select("#details-title")
    .text(`(${seat.구역})석 - 재판매 가격 분포`);

  d3.select("#details-chart").selectAll("*").remove();

  const width = 800, height = 500;
  const svgDetail = d3.select("#details-chart")
    .append("svg")
    .attr("width", width)
    .attr("height", height);

  // 🔹 X = price scale (from bin edges)
  const x = d3.scaleLinear()
    .domain([d3.min(bins, d => d.x0), d3.max(bins, d => d.x1)])
    .range([50, width - 20]);

  // 🔹 Y = frequency
  const y = d3.scaleLinear()
    .domain([0, d3.max(bins, d => d.count)])
    .range([height - 40, 20]);

  // Bars
  svgDetail.selectAll("rect")
    .data(bins)
    .enter()
    .append("rect")
    .attr("x", d => x(d.x0))
    .attr("y", d => y(d.count))
    .attr("width", d => Math.max(0, x(d.x1) - x(d.x0) - 1))
    .attr("height", d => (height - 40) - y(d.count))
    .attr("fill", "#0074d9")
    .attr("opacity", 0.7);

  // Axes
  svgDetail.append("g")
    .attr("transform", `translate(0,${height - 40})`)
    .call(d3.axisBottom(x));

  svgDetail.append("g")
    .attr("transform", `translate(50,0)`)
    .call(d3.axisLeft(y));

  // 🔹 Helper: find bin for a given value
  function findBin(value) {
    return bins.find(b => value >= b.x0 && value < b.x1);
  }

  // --- Draw helper lines (원가 + 중앙값) ---
  [[originalPrice, "green", `원가: ${originalPrice}`],
   [medianPrice, "red", `중앙값: ${medianPrice}`]]
   .forEach(([val, color, label]) => {
      const bin = findBin(val);
      if (!bin) return; // skip if no bin

      const barX = (x(bin.x0) + x(bin.x1)) / 2;   // middle of bin
      const barTop = y(bin.count);

      // Line only up to bar height
      svgDetail.append("line")
        .attr("x1", barX)
        .attr("x2", barX)
        .attr("y1", y(0))
        .attr("y2", barTop)
        .attr("stroke", color)
        .attr("stroke-dasharray", "4,4")
        .attr("stroke-width", 2);

      // Label above bar
      svgDetail.append("text")
        .attr("x", barX)
        .attr("y", barTop - 5)
        .attr("fill", color)
        .attr("font-size", "15px")
        .attr("text-anchor", "middle")
        .text(label);
   });
}


function drawCategoryBarChart(team) {
  const data = category[team];  // category JSON에서 팀 데이터 가져오기

  d3.select("#details-title")
    .text(`${team} 구역별 원가 vs 중앙 거래 가격`);

  d3.select("#details-chart").selectAll("*").remove();

  const width = 800, height = 500, margin = {top:40, right:40, bottom:80, left:80};
  const svg = d3.select("#details-chart")
    .append("svg")
    .attr("width", width)
    .attr("height", height);

  // X축: 카테고리
  const x0 = d3.scaleBand()
    .domain(data.map(d => d.카테고리))
    .range([margin.left, width - margin.right])
    .paddingInner(0.2);

  // X축 안에서 그룹(원가 vs 가격)
  const x1 = d3.scaleBand()
    .domain(["평균원가", "중앙가격"])
    .range([0, x0.bandwidth()])
    .padding(0.1);

  // Y축
  const y = d3.scaleLinear()
    .domain([0, d3.max(data, d => Math.max(d.중앙가격, d.평균원가)) * 1.1])
    .nice()
    .range([height - margin.bottom, margin.top]);

  const color = d3.scaleOrdinal()
    .domain(["평균원가", "중앙가격"])
    .range(["#2ca02c", "#ff7f0e"]); // green = 원가, orange = 가격

  // 막대 그리기
  svg.append("g")
    .selectAll("g")
    .data(data)
    .enter().append("g")
      .attr("transform", d => `translate(${x0(d.카테고리)},0)`)
    .selectAll("rect")
    .data(d => [
      {key: "평균원가", value: d.평균원가},
      {key: "중앙가격", value: d.중앙가격}
    ])
    .enter().append("rect")
      .attr("x", d => x1(d.key))
      .attr("y", d => y(d.value))
      .attr("width", x1.bandwidth())
      .attr("height", d => y(0) - y(d.value))
      .attr("fill", d => color(d.key));

  // X축 그리기
  svg.append("g")
    .attr("transform", `translate(0,${height - margin.bottom})`)
    .call(d3.axisBottom(x0))
    .selectAll("text")
      .attr("transform", "rotate(-30)")
      .style("text-anchor", "end");

  // Y축 그리기
  svg.append("g")
    .attr("transform", `translate(${margin.left},0)`)
    .call(d3.axisLeft(y));

  // 범례
  const legend = svg.append("g")
    .attr("transform", `translate(${width - margin.right - 150},${margin.top})`);

  ["평균원가", "중앙가격"].forEach((key, i) => {
    legend.append("rect")
      .attr("x", 0).attr("y", i*20)
      .attr("width", 12).attr("height", 12)
      .attr("fill", color(key));

    legend.append("text")
      .attr("x", 20).attr("y", i*20 + 10)
      .attr("alignment-baseline", "middle")
      .style("font-size", "13px")
      .text(key);
  });
}

